mapper.sql:
	where feed_back.valid=1
	
		
			and feed_back.id like CONCAT('%', #{id}, '%')
		
		
    and  DATE_FORMAT(feed_back.create_time,'%Y-%m-%d')  = DATE_FORMAT( #{createTime},'%Y-%m-%d') 
		
		
			and feed_back.status = #{status}
		
		
   and  DATE_FORMAT(feed_back.update_time,'%Y-%m-%d')  = DATE_FORMAT( #{updateTime},'%Y-%m-%d') 
		
		
			and feed_back.title like CONCAT('%', #{title}, '%')
		
		
			and feed_back.content like CONCAT('%', #{content}, '%')
		
		
			and feed_back.customer_id like CONCAT('%', #{customerId}, '%')
		
		
			and feed_back.result  like CONCAT('%', #{result}, '%')
		
		
			and feed_back.email like CONCAT('%', #{email}, '%')
		
		
			and feed_back.name like CONCAT('%', #{name}, '%')
		
		
			and feed_back.mobile= #{mobile}
		
		
			and feed_back.prefered_date = #{preferedDate}
		
		
			and feed_back.team_persons = #{teamPersons}
		
		
			and feed_back.route = #{route}
		
		
			and feed_back.sex = #{sex}
		
		
		    and feed_back.public_show=#{publicShow}

mapper.select:
select feed_back.valid,feed_back.id, feed_back.create_time, feed_back.status, feed_back.update_time, feed_back.title, feed_back.sex, rt.title route,feed_back.content, feed_back.customer_id, feed_back.result,customers.email email,customers.mobile mobile,public_show, customers.customer_name customerName from feed_back left join customers on feed_back.customer_id = customers.id left join route_template rt on rt.route_code=feed_back.route
where feed_back.valid=1 and feed_back.public_show=1
${pager.orderCondition}
${pager.mysqlQueryCondition}

select feed_back.valid,feed_back.id, feed_back.create_time, feed_back.status,feed_back.route, feed_back.update_time, feed_back.title, feed_back.sex, tr.title routeName,tr.type routeStyle,feed_back.content, feed_back.customer_id, feed_back.result,customers.email email,customers.mobile mobile,feed_back.public_show, customers.customer_name customerName from feed_back left join customers on feed_back.customer_id = customers.id left join (select travel_style.type,rt.route_code,rt.title from route_template rt left join travel_style on rt.travel_style=travel_style.id) tr on tr.route_code=feed_back.route
${pager.orderCondition}
${pager.mysqlQueryCondition}
