mapper.sql:
	where feed_back.is_valid=1
	
		
			and id like CONCAT('%', #{id}, '%')
		
		
    and  DATE_FORMAT(feed_back.create_time,'%Y-%m-%d')  = DATE_FORMAT( #{createTime},'%Y-%m-%d') 
		
		
			and status = #{status}
		
		
   and  DATE_FORMAT(feed_back.update_time,'%Y-%m-%d')  = DATE_FORMAT( #{updateTime},'%Y-%m-%d') 
		
		
			and title like CONCAT('%', #{title}, '%')
		
		
			and content like CONCAT('%', #{content}, '%')
		
		
			and customer_id like CONCAT('%', #{customerId}, '%')
		
		
			and result  like CONCAT('%', #{result}, '%')
		
		
			and email like CONCAT('%', #{email}, '%')
		
		
			and name like CONCAT('%', #{name}, '%')
		
		
			and mobile= #{mobile}
		
		
			and prefered_date = #{preferedDate}
		
		
			and team_persons = #{teamPersons}
		
		
			and route = #{route}
		
		
			and sex = #{sex}
		
		
		    and public_show=#{publicShow}

mapper.select:
select feed_back.is_valid,feed_back.id, feed_back.create_time, feed_back.status, feed_back.update_time, feed_back.title, feed_back.sex, rt.title route,feed_back.content, feed_back.customer_id, feed_back.result,customers.email email,customers.mobile mobile,public_show, customers.customer_name customerName from feed_back left join customers on feed_back.customer_id = customers.id left join route_template rt on rt.route_code=feed_back.route
${pager.orderCondition}
${pager.mysqlQueryCondition}
