mapper.sql:
where quote_form.valid=1

		and quote_form.route_template like CONCAT('%', #{routeTemplate}, '%')
		and quote_form.berief_trip like CONCAT('%', #{beriefTrip}, '%')
		and quote_form.show_trip like CONCAT('%', #{showTrip}, '%')
		and quote_form.adults=#{adults}
		and quote_form.children=#{children}
		
		    and quote_form.remark like CONCAT('%', #{remark}, '%')
		
		and quote_form.create_by=#{createBy}
		and quote_form.update_by=#{updateBy}
		and quote_form.as_adult=#{asAdult}
		and quote_form.ticket_block like CONCAT('%', #{ticketBlock}, '%')
		and quote_form.travel_docs like CONCAT('%', #{travelDocs}, '%')
		and quote_form.traveldoc_asadult=#{traveldocAsadult}
		and quote_form.tour_guide like CONCAT('%', #{tourGuide}, '%')
		and quote_form.tourguide_asadult=#{tourguideAsadult}
		and quote_form.hotel like CONCAT('%', #{hotel}, '%')
		and quote_form.hotel_place like CONCAT('%', #{hotelPlace}, '%')
		and quote_form.hotel_asadult=#{hotelAsadult}
		and quote_form.rent_car like CONCAT('%', #{rentCar}, '%')
		and quote_form.rentcar_asadult=#{rentcarAsadult}
		and quote_form.big_traffic like CONCAT('%', #{bigTraffic}, '%')
		and quote_form.big_traffic_sum=#{bigTrafficSum}
		and quote_form.bigtraffice_asadult=#{bigtrafficeAsadult}
		and quote_form.dinner like CONCAT('%', #{dinner}, '%')
		and quote_form.dinner_asadult=#{dinnerAsadult}
		and quote_form.insurance like CONCAT('%', #{insurance}, '%')
		and quote_form.insurance_asadult=#{insuranceAsadult}
		and quote_form.comprehensive_costs like CONCAT('%', #{comprehensiveCosts}, '%')
		and quote_form.comphcost_asadult=#{comphcostAsadult}
		and quote_form.recreation like CONCAT('%', #{recreation}, '%')
		and quote_form.recreation_asadult=#{recreationAsadult}
		and quote_form.item_guide like CONCAT('%', #{itemGuide}, '%')
		and quote_form.itemguide_asadult=#{itemguideAsadult}
		and quote_form.bathorse_cost like CONCAT('%', #{bathorseCost}, '%')
		and quote_form.bathcost_asadult=#{bathcostAsadult}
		and quote_form.ridehorse_cost like CONCAT('%', #{ridehorseCost}, '%')
		and quote_form.ridecost_asadult=#{ridecostAsadult}
		and quote_form.climb_register_cost like CONCAT('%', #{climbRegisterCost}, '%')
		and quote_form.climbrcost_asadult=#{climbrcostAsadult}
		and quote_form.climb_nexus_cost like CONCAT('%', #{climbNexusCost}, '%')
		and quote_form.climbncost_asadult=#{climbncostAsadult}
		and quote_form.else_cost like CONCAT('%', #{remark}, '%')
		and quote_form.elsecost_asadult=#{elsecostAsadult}
		and quote_form.presented like CONCAT('%', #{presented}, '%')
		
		   and DATE_FORMAT(quote_form.create_time,'%Y-%m-%d') = DATE_FORMAT(#{createTime},'%Y-%m-%d') 
		
		
		   and DATE_FORMAT(quote_form.update_time,'%Y-%m-%d') = DATE_FORMAT(#{updateTime},'%Y-%m-%d') 
		
		and quote_form.group_code =#{groupCode}
		
		   and DATE_FORMAT(quote_form.group_date,'%Y-%m-%d') = DATE_FORMAT(#{groupDate},'%Y-%m-%d') 
		
		and  quote_form.agoda_detail like CONCAT('%', #{agodaDetail}, '%')

mapper.update:
update quote_form set update_time=now(),
show_ticket=#{showTicket}, show_traveldoc=#{showTraveldoc}, show_tourguide=#{showTourguide}, hotel_place=#{hotelPlace}, show_hotel=#{showHotel}, show_rentcar=#{showRentcar}, show_bigtraffic=#{showBigtraffic}, show_dinner=#{showDinner}, show_insurance=#{showInsurance}, show_comphcost=#{showComphcost}, show_recreation=#{showRecreation}, show_itemguide=#{showItemguide}, show_bathorse=#{showBathorse}, show_ridehorse=#{showRidehorse}, show_climbregister=#{showClimbregister}, show_climbnexus=#{showClimbnexus}, show_elsecost=#{showElsecost}, show_presented=#{showPresented}, route_template=#{routeTemplate}, berief_trip=#{beriefTrip}, show_trip =#{showTrip}, adults=#{adults}, children=#{children}, remark=#{remark}, create_by=#{createBy}, update_by=#{updateBy}, as_adult=#{asAdult}, ticket_block=#{ticketBlock}, ticket_asadult=#{ticketAsadult}, travel_docs=#{travelDocs}, traveldoc_asadult=#{traveldocAsadult}, tour_guide=#{tourGuide}, tourguide_asadult=#{tourguideAsadult}, hotel=#{hotel}, hotel_asadult=#{hotelAsadult}, rent_car=#{rentCar}, rentcar_asadult=#{rentcarAsadult}, big_traffic=#{bigTraffic}, big_traffic_sum=#{bigTrafficSum}, bigtraffice_asadult=#{bigtrafficeAsadult}, dinner=#{dinner}, dinner_asadult=#{dinnerAsadult}, insurance=#{insurance}, insurance_asadult=#{insuranceAsadult}, comprehensive_costs=#{comprehensiveCosts}, comphcost_asadult=#{comphcostAsadult}, recreation=#{recreation}, recreation_asadult=#{recreationAsadult}, item_guide=#{itemGuide}, itemguide_asadult=#{itemguideAsadult}, bathorse_cost=#{bathorseCost}, bathcost_asadult=#{bathcostAsadult}, ridehorse_cost=#{ridehorseCost}, ridecost_asadult=#{ridecostAsadult}, climb_register_cost=#{climbRegisterCost}, climbrcost_asadult=#{climbrcostAsadult}, climb_nexus_cost=#{climbNexusCost}, climbncost_asadult=#{climbncostAsadult}, else_cost=#{elseCost}, elsecost_asadult=#{elsecostAsadult}, presented=#{presented}, group_code =#{groupCode}, group_date= #{groupDate},
agoda_detail=#{agodaDetail}, where id=#{id}
