mapper.sql:
where route_template.valid=1 

	
		and route_template.elevation=#{elevation}
	
	
		and route_template.similars=#{similars}
	
	
		and route_template.difficulty_rate=#{difficultyRate}
	
	
	    and route_template.route_code = #{routeCode}
	
	
	    and route_template.title like CONCAT('%', #{title}, '%')
	
	
	    and route_template.short_content like CONCAT('%', #{shortContent}, '%')
	
	
		and route_template.alias like CONCAT('%', #{alias}, '%')
	
	
	    and route_template.customer_id like CONCAT('%', #{customerId}, '%')
	
	
	     and DATE_FORMAT(route_template.create_time,'%Y-%m-%d') = DATE_FORMAT(#{createTime},'%Y-%m-%d') 
	
	
	    and DATE_FORMAT(route_template.update_time,'%Y-%m-%d') = DATE_FORMAT(#{updateTime},'%Y-%m-%d') 
	
	
	    and route_template.create_by like CONCAT('%', #{createBy}, '%')
	
	
	    and route_template.update_by like CONCAT('%', #{updateBy}, '%')
	
	
	    and route_template.remark like CONCAT('%', #{remark}, '%')
	
	
		and route_template.special like  CONCAT('%', #{special}, '%')
	
	
		and route_template.related like  CONCAT('%', #{related}, '%')
	
	
		and route_template.route_map = #{routeMap}
	
	
		and route_template.travel_style = #{travelStyle}
	
	
		and route_template.travel_items=#{travelItems}
	
	
		and route_template.rcd_days=#{rcdDays}
	
	
		and route_template.mileage=#{mileage}
	
	
		and route_template.arrive=#{arrive}
	
	
		and route_template.departure=#{departure}
	
	
		and route_template.mount_style=#{mountStyle}
	
	
		and route_template.trek_distance=#{trekDistance}
	
	
		and route_template.transportation=#{transportation}
	
	
		and route_template.level_area=#{levelArea}
	
	
		and route_template.star_level=#{starLevel}
	
	
		and route_template.viewphotos=#{viewphotos}

mapper.select:
select from route_template where route_template.id in
#{ids}
order by star_level desc

select route_template.id,route_template.cover,route_template.title,route_template.short_content,route_template.alias,route_code,star_level, similars,level_area,viewphotos,route_template.customer_id, route_template.create_time, route_template.update_time, concat(cb.nickName,'(',cb.email,')') create_by, concat(ub.nickName,'(',ub.email,')') update_by, elevation,la.level1_area,la.level2_area, route_template.remark,route_template.special,(select rt.title from route_template rt where rt.id=route_template.related) related,route_template.route_map,travel_style.type travel_style,route_template.travel_items,route_template.rcd_days,route_template.mileage,route_template.arrive,route_template.departure, route_template.mount_style,route_template.trek_distance,route_template.transportation,route_template.difficulty_rate,route_template.quoto_form,route_template.design_concept,route_template.customized_service,route_template.before_instruction
from route_template left join travel_style on route_template.travel_style = travel_style.id left join sys_user cb on cb.id=route_template.create_by left join sys_user ub on ub.id=route_template.update_by left join level_area la on la.id=route_Template.level_area
