mapper.select:
select
,(SELECT COUNT(1) FROM sys_menu c WHERE c.parentId = m.id and deleted = 0) AS subCount, (select concat(u.nickname,'(',u.email,')') from sys_user u left join sys_menu menu on u.id=menu.create_by where menu.deleted = 0 and menu.id=m.id)as create_by, (select concat(u.nickname,'(',u.email,')') from sys_user u left join sys_menu menu on u.id=menu.update_by where menu.deleted = 0 and menu.id=m.id)as update_by from sys_menu m
${pager.orderCondition}
${pager.mysqlQueryCondition}

	select    from sys_menu where deleted= 0 and parentId is null or parentId = ''
	 
     and  id != #{menuId}
     
    order by rank asc

select from sys_menu where deleted= 0 and parentId is not null and parentId != ''
and parentId = #{parentId}
order by rank asc

SELECT  FROM sys_menu m WHERE deleted= 0 AND parentId IS NOT NULL and parentId != ''
AND EXISTS (
	 SELECT * FROM sys_role_rel r WHERE r.objId = m.id AND relType = 0
	 
		 AND r.roleId = #{roleId}
	 
)
order by rank asc

SELECT DISTINCT id,NAME,url,parentId,actions FROM sys_menu m WHERE deleted= 0 AND (parentId IS NULL or parentId ='') AND EXISTS ( SELECT * FROM sys_role_rel r WHERE r.objId = m.id AND r.relType = 0 AND EXISTS ( SELECT 1 FROM sys_role_rel u WHERE u.roleId = r.roleId AND u.relType = 1
AND u.objId = #{userId}
) ) order by rank asc

SELECT DISTINCT id,NAME,url,parentId,actions FROM sys_menu m WHERE deleted= 0 AND parentId IS NOT NULL and parentId !='' AND EXISTS ( SELECT * FROM sys_role_rel r WHERE r.objId = m.id AND r.relType = 0 AND EXISTS ( SELECT 1 FROM sys_role_rel u WHERE u.roleId = r.roleId AND u.relType = 1
AND u.objId = #{userId}
) ) order by rank asc
